// TemperatureChart.js
import React, { useState, useEffect } from 'react';
import { getDatabase, ref, onValue } from 'firebase/database';
import { app } from "../../firebase"; // Adjust the path according to your project structure
import { Line } from 'react-chartjs-2';
import { Chart, registerables } from 'chart.js';
import './TemperatureChart.css'; // Ensure this path is correct

Chart.register(...registerables);

const database = getDatabase(app);

const TemperatureChart = () => {
  const [chartData, setChartData] = useState({
    labels: [],
    ot: [],
    lvt: [],
    lv1t: [],
    lv2t: [],
    hvt: []
  });

  useEffect(() => {
    const dataRef = ref(database, 'realtimedata');
    const unsubscribe = onValue(dataRef, (snapshot) => {
      if (snapshot.exists()) {
        const newData = snapshot.val();

        setChartData(prevState => ({
          labels: [...prevState.labels, new Date().toLocaleTimeString()],
          ot: [...prevState.ot, newData.ot],
          lvt: [...prevState.lvt, newData.lvt],
          lv1t: [...prevState.lv1t, newData.lv1t],
          lv2t: [...prevState.lv2t, newData.lv2t],
          hvt: [...prevState.hvt, newData.hvt]
        }));
      }
    });

    return () => {
      unsubscribe();
    };
  }, []);

  const lineOptions = {
    responsive: true,
    maintainAspectRatio: false,
    plugins: {
      legend: {
        display: true,
        position: 'top'
      },
      tooltip: {
        mode: 'index',
        intersect: false
      }
    },
    scales: {
      x: {
        display: true,
        title: {
          display: true,
          text: 'Time'
        }
      },
      y: {
        display: true,
        title: {
          display: true,
          text: 'Temperature (°C)'
        }
      }
    }
  };

  const renderLineChart = (data, label, borderColor, backgroundColor) => (
    <div className="chart-container">
      <h4>{label}</h4>
      <Line
        data={{
          labels: chartData.labels,
          datasets: [{
            label,
            data,
            borderColor,
            backgroundColor,
            borderWidth: 2,
            fill: true
          }]
        }}
        options={lineOptions}
      />
    </div>
  );

  return (
    <div className="temperature-chart-wrapper">
      <h3>Real-Time Temperature Data</h3>
      {renderLineChart(chartData.ot, 'Oil Temperature', 'rgba(75,192,192,1)', 'rgba(75,192,192,0.2)')}
      {renderLineChart(chartData.lvt, 'LV Winding Temperature', 'rgba(54, 162, 235, 1)', 'rgba(54, 162, 235, 0.2)')}
      {renderLineChart(chartData.lv1t, 'LV1 Winding Temperature', 'rgba(255, 206, 86, 1)', 'rgba(255, 206, 86, 0.2)')}
      {renderLineChart(chartData.lv2t, 'LV2 Winding Temperature', 'rgba(153, 102, 255, 1)', 'rgba(153, 102, 255, 0.2)')}
      {renderLineChart(chartData.hvt, 'HV Winding Temperature', 'rgba(255, 99, 132, 1)', 'rgba(255, 99, 132, 0.2)')}
    </div>
  );
}

export default TemperatureChart;
