// index.jsx
import React, { useState, useEffect } from 'react';
import { Map, GoogleApiWrapper, Marker } from 'google-maps-react';
import { database } from '../../firebase'; // Adjust the path as per your project structure
import { getDatabase, ref, onValue, off } from "firebase/database";

const MapContainer = (props) => {
  const [coordinates, setCoordinates] = useState({ lat: 0, lng: 0 });

  useEffect(() => {
    const fetchCoordinates = () => {
      const coordinatesRef = ref(database, 'coordinates'); // Reference to 'coordinates' node
      onValue(coordinatesRef, (snapshot) => {
        const coords = snapshot.val();
        if (coords) {
          setCoordinates(coords); // Set coordinates state
        }
      });

      // Cleanup function to detach the listener when component unmounts
      return () => {
        off(coordinatesRef); // Detach listener
      };
    };

    fetchCoordinates();
  }, []);

  return (
    <Map
      google={props.google}
      zoom={14} // Adjust zoom level as needed
      initialCenter={{ lat: coordinates.lat, lng: coordinates.lng }}
    >
      <Marker position={{ lat: coordinates.lat, lng: coordinates.lng }} />
    </Map>
  );
};

export default GoogleApiWrapper({
  apiKey: 'AIzaSyBt0EBeEOhb6s_nvQqPBh_vYMFQXFG6gBc' // Replace with your Google Maps API key
})(MapContainer);
