import React, { useState, useEffect } from 'react';
import { getDatabase, ref, onValue } from 'firebase/database';
import { useNavigate } from 'react-router-dom';
import { app } from './firebase.js'; // Assuming this is your Firebase config file
import "./HomePage.css"
const database = getDatabase(app);

function HomePage() {
  const [data, setData] = useState({});
  const [coordinates, setCoordinates] = useState({ lat: '', lng: '' });
  const navigate = useNavigate();

  useEffect(() => {
    const dataRef = ref(database, 'realtimedata');
    const unsubscribe = onValue(dataRef, (snapshot) => {
      if (snapshot.exists()) {
        setData(snapshot.val());
      }
    });

    const coordRef = ref(database, 'coordinates');
    const coordUnsubscribe = onValue(coordRef, (snapshot) => {
      if (snapshot.exists()) {
        setCoordinates(snapshot.val());
      }
    });

    return () => {
      unsubscribe();
      coordUnsubscribe();
    };
  }, []);

  const handleRedirect = () => {
    // Replace with your desired link
    window.location.href = 'https://example.com';
  };

  return (
    <div className="homepage">
      <header>
        <img src="/path/to/your-logo.png" alt="Company Logo" className="logo" />
      </header>

      <main>
        <div className="data-box">
          <h2>Real-time Data</h2>
          <div class="data-row">
            <span class="data-label">Oil Temperature:</span>
            <span class="data-value" data-field={data.ot} >{data.ot}</span>
          </div>
          <div class="data-row">
            <span class="data-label">lv Winding Temperature:</span>
            <span class="data-value" data-field="lvt"></span>
          </div>
          <div class="data-row">
            <span class="data-label">lv1 Winding Temperature:</span>
            <span class="data-value" data-field="lv1t"></span>
          </div>
          <div class="data-row">
            <span class="data-label">lv2 Winding Temperature:</span>
            <span class="data-value" data-field="lv2t"></span>
          </div>
          <div class="data-row">
            <span class="data-label">hv Winding Temperature:</span>
            <span class="data-value" data-field="hvt"></span>
          </div>
          <div class="data-row">
            <span class="data-label">Humidity:</span>
            <span class="data-value" data-field="hum"></span>
          </div>
          <div class="data-row" id="otha-row">
            <span class="data-label">Oil temperature high alarm</span>
          </div>
          <div class="data-row" id="lvtha-row">
            <span class="data-label">Winding temperature high alarm</span>
          </div>
          <div class="data-row" id="gora-row">
            <span class="data-label">GOR alarm</span>
          </div>
          <div class="data-row" id="moga-row">
            <span class="data-label">Oil level low alarm</span>
          </div>
          <div class="data-row" id="rly1-row">
            <span class="data-label">Cooler on indication</span>
          </div>
          <p>OT: {data.ot}</p>
          <p>LVT: {data.lvt}</p>
          <p>LV1T: {data.lv1t}</p>
          <p>LV2T: {data.lv2t}</p>
          <p>HVT: {data.hvt}</p>
          <p>HUM: {data.hum}</p>
          <p>OTHA: {data.otha}</p>
          <p>LVTHA: {data.lvtha}</p>
          <p>GORA: {data.gora}</p>
          <p>MOGA: {data.moga}</p>
          <p>RLY1: {data.rly1}</p>
        </div>

        <button onClick={handleRedirect} className="redirect-button">
          Go to Link
        </button>

        <div className="coordinates">
          <h3>Current Coordinates</h3>
          <p>Latitude: {coordinates.lat}</p>
          <p>Longitude: {coordinates.lng}</p>
        </div>
      </main>
    </div>
  );
}

export default HomePage;
