// TemperatureDisplay.js
import React, { useEffect, useState } from 'react';
import { database } from '../../firebase'; // Adjust path to firebase.js as per your project structure
import { ref, onValue, off } from "firebase/database";
import './Temp.css'; // Import CSS file for styling

const TemperatureDisplay = () => {
  const [temperatures, setTemperatures] = useState([]);

  useEffect(() => {
    const temperaturesRef = ref(database, 'realtimedata');

    // Attach an event listener for changes in temperatures
    const unsubscribe = onValue(temperaturesRef, (snapshot) => {
      const tempData = snapshot.val();
      if (tempData) {
        // Convert the snapshot to an array of objects
        const tempArray = Object.keys(tempData).map((key) => ({
          id: key,
          ...tempData[key],
        }));
        setTemperatures(tempArray);
      }
    });

    // Clean up listener on component unmount
    return () => {
      off(temperaturesRef);
    };
  }, []);

  return (
    <div className="temperature-container">
      <h2>Real-Time Temperature Values</h2>
      <table className="temperature-table">
        <thead>
          <tr>
            <th>Time</th>
            <th>Oil Temperature</th>
            <th>LV Winding Temperature</th>
            <th>LV1 Winding Temperature</th>
            <th>LV2 Winding Temperature</th>
            <th>HV Winding Temperature</th>
          </tr>
        </thead>
        <tbody>
          {temperatures.map((temp) => (
            <tr key={temp.id} className="temperature-row">
              <td>{new Date(temp.timestamp).toLocaleString()}</td>
              <td>{temp.ot}</td>
              <td>{temp.lvt}</td>
              <td>{temp.lv1t}</td>
              <td>{temp.lv2t}</td>
              <td>{temp.hvt}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default TemperatureDisplay;
