import React, { useState, useEffect } from 'react';
import { initializeApp } from 'firebase/app';
import { getDatabase, ref, onValue } from 'firebase/database';
import { useNavigate } from 'react-router-dom';

// Initialize Firebase (replace with your config)
const firebaseConfig = {
  apiKey: "AIzaSyCEJwpQQGzWGf4WaetQrOKWOAEDuYtq6uE",
  authDomain: "hps-transformer.firebaseapp.com",
  databaseURL: "https://hps-transformer-default-rtdb.asia-southeast1.firebasedatabase.app",
  projectId: "hps-transformer",
  storageBucket: "hps-transformer.appspot.com",
  messagingSenderId: "408759929751",
  appId: "1:408759929751:web:b667c126376b3d233492d2",
  measurementId: "G-LCQM85SQ3E"
};

const app = initializeApp(firebaseConfig);
const database = getDatabase(app);

function HomePage() {
  const [data, setData] = useState({});
  const [coordinates, setCoordinates] = useState({ lat: '', lng: '' });
  const navigate = useNavigate();

  useEffect(() => {
    const dataRef = ref(database, 'realtime-data');
    const unsubscribe = onValue(dataRef, (snapshot) => {
      if (snapshot.exists()) {
        setData(snapshot.val());
      }
    });

    const coordRef = ref(database, 'coordinates');
    const coordUnsubscribe = onValue(coordRef, (snapshot) => {
      if (snapshot.exists()) {
        setCoordinates(snapshot.val());
      }
    });

    return () => {
      unsubscribe();
      coordUnsubscribe();
    };
  }, []);

  const handleRedirect = () => {
    // Replace with your desired link
    window.location.href = 'https://example.com';
  };

  return (
    <div className="homepage">
      <header>
        <img src="/path/to/your-logo.png" alt="Company Logo" className="logo" />
      </header>
      
      <main>
        <div className="data-box">
          <h2>Real-time Data</h2>
          <p>OT: {data.ot}</p>
          <p>LVT: {data.lvt}</p>
          <p>LV1T: {data.lv1t}</p>
          <p>LV2T: {data.lv2t}</p>
          <p>HVT: {data.hvt}</p>
          <p>HUM: {data.hum}</p>
          <p>OTHA: {data.otha}</p>
          <p>LVTHA: {data.lvtha}</p>
          <p>GORA: {data.gora}</p>
          <p>MOGA: {data.moga}</p>
          <p>RLY1: {data.rly1}</p>
        </div>
        
        <button onClick={handleRedirect} className="redirect-button">
          Go to Link
        </button>
        
        <div className="coordinates">
          <h3>Current Coordinates</h3>
          <p>Latitude: {coordinates.lat}</p>
          <p>Longitude: {coordinates.lng}</p>
        </div>
      </main>
    </div>
  );
}

export default HomePage;
