// src/components/LiveLocationMap.js
import React, { useState, useEffect } from 'react';
import { GoogleMap, Marker, useLoadScript } from '@react-google-maps/api';
import { getDatabase, ref, onValue } from 'firebase/database';
import { app } from '../../firebase'; // Adjust the path as needed

const mapContainerStyle = {
  width: '100%',
  height: '400px'
};

const center = {
  lat: 0, // Initial center latitude
  lng: 0 // Initial center longitude
};

const LiveLocationMap = () => {
    const [coordinates, setCoordinates] = useState(center);
    const { isLoaded, loadError } = useLoadScript({
      googleMapsApiKey: process.env.REACT_APP_GOOGLE_MAPS_API_KEY
    });
    const database = getDatabase(app);
  
    useEffect(() => {
      const coordRef = ref(database, 'coordinates');
      const unsubscribe = onValue(coordRef, (snapshot) => {
        if (snapshot.exists()) {
          const coords = snapshot.val();
          setCoordinates({
            lat: parseFloat(coords.lat), // Ensure coordinates are parsed as floats
            lng: parseFloat(coords.lng)
          });
        }
      }, {
        onlyOnce: true // unsubscribe after the initial value is fetched
      });
  
      return () => unsubscribe();
    }, [database]);
  
    if (loadError) return <div>Error loading maps</div>;
    if (!isLoaded) return <div>Loading maps...</div>;
  
    return (
      <GoogleMap
        mapContainerStyle={mapContainerStyle}
        zoom={15}
        center={coordinates}
      >
        <Marker position={coordinates} />
      </GoogleMap>
    );
  };

  export default LiveLocationMap;
