import React, { useState, useEffect } from 'react';
import { initializeApp } from 'firebase/app';
import { getDatabase, ref, onValue } from 'firebase/database';
import { useNavigate } from 'react-router-dom';
import app from "./firebase"
import HPS_logo from "../assets/HPS_logo.webp"
import "./HomePage.css"

const database = getDatabase(app);

function HomePage() {
  const [data, setData] = useState({});
  const [coordinates, setCoordinates] = useState({ lat: '', lng: '' });
  const navigate = useNavigate();

  useEffect(() => {
    const dataRef = ref(database, 'realtime-data');
    const unsubscribe = onValue(dataRef, (snapshot) => {
      if (snapshot.exists()) {
        setData(snapshot.val());
      }
    });

    const coordRef = ref(database, 'coordinates');
    const coordUnsubscribe = onValue(coordRef, (snapshot) => {
      if (snapshot.exists()) {
        setCoordinates(snapshot.val());
      }
    });

    return () => {
      unsubscribe();
      coordUnsubscribe();
    };
  }, []);

  const handleRedirect = () => {
    // Replace with your desired link
    window.location.href = 'https://example.com';
  };

  return (
    <div className="homepage">
      <header>
        {/* <img src={HPS_logo} alt="" width={100} height={50}/> */}
        <img src={HPS_logo} alt="" className='logo'/>
      </header>
      
      <main>
        <div className="data-box">
          <h2>Real-time Data</h2>
          <p>OT: {data.ot}</p>
          <p>LVT: {data.lvt}</p>
          <p>LV1T: {data.lv1t}</p>
          <p>LV2T: {data.lv2t}</p>
          <p>HVT: {data.hvt}</p>
          <p>HUM: {data.hum}</p>
          <p>OTHA: {data.otha}</p>
          <p>LVTHA: {data.lvtha}</p>
          <p>GORA: {data.gora}</p>
          <p>MOGA: {data.moga}</p>
          <p>RLY1: {data.rly1}</p>
        </div>
        
        <button onClick={handleRedirect} className="redirect-button">
          Get Data_log
        </button>
        
        <div className="coordinates">
          <h3>Current Coordinates</h3>
          <p>Latitude: {coordinates.lat}</p>
          <p>Longitude: {coordinates.lng}</p>
        </div>
      </main>
    </div>
  );
}

export default HomePage;
