import React, { useState, useEffect } from 'react';
import { getDatabase, ref, onValue } from 'firebase/database';
import { Map, GoogleApiWrapper, Marker } from 'google-maps-react';
import { app } from '../../firebase'; // Adjust the path as per your project structure

const database = getDatabase(app);

const MapContainer = (props) => {
  const [coordinates, setCoordinates] = useState({ latitude: 0, longitude: 0 });

  useEffect(() => {
      const coordinatesRef = ref(database, 'coordinates');
      const unsubscribe = onValue(coordinatesRef, (snapshot) => {
        const coords = snapshot.val();
        if (coords) {
          setCoordinates(coords); // Assuming coords has { lat, lng } structure
        }
      });
    

    

    // Cleanup function to detach the listener when component unmounts
    return () => {
        unsubscribe();
      };
  }, []);

  return (
    <Map
      google={props.google}
      zoom={14} // Adjust zoom level as needed
      initialCenter={{ lat: coordinates.lat, lng: coordinates.lng }}
    >
      <Marker position={{ lat: coordinates.lat, lng: coordinates.lng }} />
    </Map>
  );
};

export default GoogleApiWrapper({
  apiKey: 'AIzaSyBt0EBeEOhb6s_nvQqPBh_vYMFQXFG6gBc'
})(MapContainer);
