import React, { useEffect, useState } from 'react';
import { Line } from 'react-chartjs-2';
import { getDatabase, ref, onValue } from 'firebase/database';
import { database } from '../../firebase';
import 'chart.js/auto';

const TemperatureChart = () => {
  const [chartData, setChartData] = useState({
    labels: [],
    datasets: [
      {
        label: 'Temperature',
        data: [],
        borderColor: 'rgba(75, 192, 192, 1)',
        backgroundColor: 'rgba(75, 192, 192, 0.2)',
        fill: true,
        tension: 0.1,
      },
    ],
  });

  useEffect(() => {
    const fetchTemperatureData = () => {
      const dbRef = ref(getDatabase(database), 'realtimedata/ot');
      onValue(dbRef, (snapshot) => {
        const data = snapshot.val();
        if (data) {
          let temperatures = [];
          let timestamps = [];

          // Assuming data is an array of temperature values
          Object.values(data).forEach((temp) => {
            temperatures.push(temp);
            // Assuming you want to display a timestamp for each temperature
            timestamps.push(new Date().toLocaleString()); // You can customize this timestamp as needed
          });

          // Update chart data
          setChartData({
            labels: timestamps,
            datasets: [
              {
                label: 'Temperature',
                data: temperatures,
                borderColor: 'rgba(75, 192, 192, 1)',
                backgroundColor: 'rgba(75, 192, 192, 0.2)',
                fill: true,
                tension: 0.1,
              },
            ],
          });
        }
      });
    };

    fetchTemperatureData();
  }, []);

  return (
    <div>
      <h2>Temperature Chart</h2>
      <Line data={chartData} />
    </div>
  );
};

export default TemperatureChart;
