// TemperatureChart.js
import React, { useState, useEffect } from 'react';
import { getDatabase, ref, onValue } from 'firebase/database';
import { app } from "../../firebase"; // Adjust the path according to your project structure
import { Line } from 'react-chartjs-2';
import { Chart, registerables } from 'chart.js';
import './TemperatureChart.css'; // Ensure this path is correct

Chart.register(...registerables);

const database = getDatabase(app);

const MAX_DATA_POINTS = 20; // Set the maximum number of data points to display

const TemperatureChart = () => {
  const [chartData, setChartData] = useState({
    labels: [],
    ot: [],
    lvt: [],
    lv1t: [],
    lv2t: [],
    hvt: []
  });

  useEffect(() => {
    const dataRef = ref(database, 'realtimedata');
    const unsubscribe = onValue(dataRef, (snapshot) => {
      if (snapshot.exists()) {
        const newData = snapshot.val();

        setChartData(prevState => {
          const labels = [...prevState.labels, new Date().toLocaleTimeString()];
          const ot = [...prevState.ot, newData.ot];
          const lvt = [...prevState.lvt, newData.lvt];
          const lv1t = [...prevState.lv1t, newData.lv1t];
          const lv2t = [...prevState.lv2t, newData.lv2t];
          const hvt = [...prevState.hvt, newData.hvt];

          // Ensure arrays do not exceed the maximum number of data points
          if (labels.length > MAX_DATA_POINTS) {
            labels.shift();
            ot.shift();
            lvt.shift();
            lv1t.shift();
            lv2t.shift();
            hvt.shift();
          }

          return {
            labels,
            ot,
            lvt,
            lv1t,
            lv2t,
            hvt
          };
        });
      }
    });

    return () => {
      unsubscribe();
    };
  }, []);

  const lineOptions = {
    responsive: true,
    maintainAspectRatio: false,
    plugins: {
      legend: {
        display: false // Hide legend to match the example image
      },
      tooltip: {
        mode: 'index',
        intersect: false
      }
    },
    scales: {
      x: {
        display: false // Hide x-axis to match the example image
      },
      y: {
        display: false // Hide y-axis to match the example image
      }
    }
  };

  const renderLineChart = (data, label, borderColor, backgroundColor, summary, percentage) => (
    <div className="chart-container">
      <h4>{label}</h4>
      <div className="summary">{summary}</div>
      <div className="summary-sub">This month</div>
      <div className={`percentage ${percentage < 0 ? 'negative' : ''}`}>{percentage}%</div>
      <Line
        data={{
          labels: chartData.labels,
          datasets: [{
            label,
            data,
            borderColor,
            backgroundColor,
            borderWidth: 2,
            fill: true
          }]
        }}
        options={lineOptions}
      />
    </div>
  );

  return (
    <div className="temperature-chart-wrapper">
      {renderLineChart(chartData.ot, 'Oil Temperature', 'rgba(75,192,192,1)', 'rgba(75,192,192,0.2)', '11.238', 45)}
      {renderLineChart(chartData.lvt, 'LV Winding Temperature', 'rgba(54, 162, 235, 1)', 'rgba(54, 162, 235, 0.2)', '$56.432', -12)}
      {renderLineChart(chartData.lv1t, 'LV1 Winding Temperature', 'rgba(255, 206, 86, 1)', 'rgba(255, 206, 86, 0.2)', '238', 21)}
      {renderLineChart(chartData.lv2t, 'LV2 Winding Temperature', 'rgba(153, 102, 255, 1)', 'rgba(153, 102, 255, 0.2)', '2.6', 12)}
      {renderLineChart(chartData.hvt, 'HV Winding Temperature', 'rgba(255, 99, 132, 1)', 'rgba(255, 99, 132, 0.2)', '15.8', 8)}
    </div>
  );
}

export default TemperatureChart;
