import { useEffect, useState } from 'react';
import { getDatabase, ref, onValue, off } from 'firebase/database';
import { Line } from 'react-chartjs-2';
import { initializeApp } from 'firebase/app';
import { app } from '../../firebase'; // Adjust path to your Firebase config file


const db = getDatabase(app);

const TemperatureGraph = () => {
  const [temperatures, setTemperatures] = useState([]);

  useEffect(() => {
    const fetchData = () => {
      const temperatureRef = ref(db, 'realtimedata/ot');

      onValue(temperatureRef, (snapshot) => {
        const data = snapshot.val();
        if (data) {
          const temperatureValues = Object.values(data);
          setTemperatures(temperatureValues);
        }
      });
    };

    fetchData();

    // Clean up listener on unmount
    return () => {
      const temperatureRef = ref(db, 'realtimedata/ot');
      off(temperatureRef);
    };
  }, []);

  // Prepare data for Chart.js
  const data = {
    labels: temperatures.map((_, index) => `Data Point ${index + 1}`),
    datasets: [
      {
        label: 'Temperature Data',
        fill: false,
        lineTension: 0.1,
        backgroundColor: 'rgba(75,192,192,0.4)',
        borderColor: 'rgba(75,192,192,1)',
        borderCapStyle: 'butt',
        borderDash: [],
        borderDashOffset: 0.0,
        borderJoinStyle: 'miter',
        pointBorderColor: 'rgba(75,192,192,1)',
        pointBackgroundColor: '#fff',
        pointBorderWidth: 1,
        pointHoverRadius: 5,
        pointHoverBackgroundColor: 'rgba(75,192,192,1)',
        pointHoverBorderColor: 'rgba(220,220,220,1)',
        pointHoverBorderWidth: 2,
        pointRadius: 1,
        pointHitRadius: 10,
        data: temperatures,
      },
    ],
  };

  return (
    <div>
      <h2>Real-time Temperature Graph</h2>
      <Line data={data} />
    </div>
  );
};

export default TemperatureGraph;
