import React, { useContext, useEffect } from "react";
import { Box, IconButton, Button, useTheme } from "@mui/material";
import { ColorModeContext, tokens } from "../theme";
import LightModeOutlinedIcon from "@mui/icons-material/LightModeOutlined";
import DarkModeOutlinedIcon from "@mui/icons-material/DarkModeOutlined";
import * as XLSX from "xlsx";
import { gapi } from "gapi-script";

const CLIENT_ID = '311656800637-3lp3k7ilbp7m89kfa6tcmlnvgc223jr5.apps.googleusercontent.com';
const API_KEY = 'AIzaSyCS3R-wjWo9THGLf2vzSgsTKPkER1SeXhc';
const SPREADSHEET_ID = '1eTTOYMgIiHgZMSLgzKSmXpTTfPb4_a0MqoRsyorvXVk';
const SHEET_NAME = 'HPS';

const Topbar = () => {
  const theme = useTheme();
  const colors = tokens(theme.palette.mode);
  const colorMode = useContext(ColorModeContext);

  useEffect(() => {
    function start() {
      gapi.client.init({
        apiKey: API_KEY,
        clientId: CLIENT_ID,
        discoveryDocs: ["https://sheets.googleapis.com/$discovery/rest?version=v4"],
        scope: "https://www.googleapis.com/auth/spreadsheets.readonly",
      });
    }
    gapi.load("client:auth2", start);
  }, []);

  const downloadSheetAsExcel = async () => {
    try {
      const response = await gapi.client.sheets.spreadsheets.values.get({
        spreadsheetId: SPREADSHEET_ID,
        range: SHEET_NAME,
      });

      const data = response.result.values;
      const ws = XLSX.utils.aoa_to_sheet(data);
      const wb = XLSX.utils.book_new();
      XLSX.utils.book_append_sheet(wb, ws, SHEET_NAME);
      XLSX.writeFile(wb, "SheetData.xlsx");
    } catch (error) {
      console.error("Error downloading sheet: ", error);
    }
  };

  return (
    <Box
      display="flex"
      justifyContent="space-between"
      alignItems="center"
      p={2}
      bgcolor={colors.primary[400]}
      boxShadow={1}
      zIndex={1}
      position="sticky"
      top={0}
      width="100%"
    >
      <IconButton onClick={colorMode.toggleColorMode}>
        {theme.palette.mode === "dark" ? (
          <DarkModeOutlinedIcon />
        ) : (
          <LightModeOutlinedIcon />
        )}
      </IconButton>
      <Button variant="contained" onClick={downloadSheetAsExcel}>
        Download Sheet
      </Button>
    </Box>
  );
};

export default Topbar;
